sap.ui.define(["hckt/manifest/controller/baseController"],function(e){return e.extend("hckt.manifest.controller.detail",{onInit:function(){this.oRouter=this.getOwnerComponent().getRouter();this.oRouter.attachRoutePatternMatched(this.bindElement,this)},bindElement:function(e){var t=e.getParameters().arguments.productId;if(e.getParameters().arguments.type==="fruits"){t="/fruits/"+t;this.getView().bindElement(t);this.getView().byId("idFlowerForm").setVisible(false);this.getView().byId("idFruitForm").setVisible(true)}else{t="/flowers/"+t;this.getView().bindElement(t);this.getView().byId("idFruitForm").setVisible(false);this.getView().byId("idFlowerForm").setVisible(true)}},onBack:function(){this.oRouter.navTo("master")}})});